import React, { useState, useEffect, createContext } from 'react';
import axios from 'axios';
import { w3cwebsocket as W3CWebSocket } from 'websocket';

export const KernelContext = createContext();

export function KernelManager({ children }) {
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    const createKernel = async () => {
      if (!socket) { // Only create a new kernel if one doesn't already exist
        try {
          const response = await axios.post('http://localhost:8888/api/kernels?token=123456');
          const kernelId = response.data.id;
          const newSocket = new W3CWebSocket(`ws://localhost:8888/api/kernels/${kernelId}/channels?token=123456`);
          setSocket(newSocket);
        } catch (error) {
          console.error('Failed to create kernel:', error);
        }
      }
    };

    createKernel();

    return () => {
      if (socket) {
        socket.close();
      }
    };
  }, [socket]); // Add socket to the dependency array

  return (
    <KernelContext.Provider value={socket}>
      {children}
    </KernelContext.Provider>
  );
}
