import React, { useState, useEffect } from 'react';
import AceEditor from 'react-ace';
import axios from 'axios';

import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-monokai';

function CodeCell() {
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [kernelId, setKernelId] = useState(null);

  useEffect(() => {
    const createKernel = async () => {
      try {
        const response = await axios.post('http://localhost:8888/api/kernels?token=123456');
        setKernelId(response.data.id);
      } catch (error) {
        console.error('Failed to create kernel:', error);
      }
    };

    createKernel();
  }, []);

  const handleExecute = async () => {
    if (kernelId) {
      try {
        const response = await axios.post(`http://localhost:8888/api/kernels/${kernelId}/execute?token=123456`, {
          code,
        });
        setOutput(response.data);
      } catch (error) {
        setOutput(error.toString());
      }
    }
  };

  return (
    <div>
      <AceEditor
        mode="python"
        theme="monokai"
        onChange={setCode}
        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
      <button onClick={handleExecute} disabled={!kernelId}>
        Run
      </button>
      <pre>{output}</pre>
    </div>
  );
}

export default CodeCell;
