import React, { useState } from 'react';
import AceEditor from 'react-ace';
import axios from 'axios';

import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-monokai';

function CopilotCell() {
  const [code, setCode] = useState('');

  const handleGenerate = async () => {
    try {
      const response = await axios.post('https://hkust.azure-api.net/v1/engines/davinci-codex/completions', {
        prompt: code,
        max_tokens: 100,
      }, {
        headers: {
          'Authorization': 'f0f00b34e41f4bb4b68ad79231843c9e',
          'Content-Type': 'application/json',
        },
      });

      const generatedCode = response.data.choices[0].text;
      setCode(generatedCode);
    } catch (error) {
      console.error('Failed to generate code:', error);
    }
  };

  return (
    <div>
      <AceEditor
        mode="python"
        theme="monokai"
        onChange={setCode}
        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
      <button onClick={handleGenerate}>
        Generate Code
      </button>
    </div>
  );
}

export default CopilotCell;
