import React, { useState, useEffect, useContext } from 'react';
import AceEditor from 'react-ace';
import { KernelContext } from './KernelProvide';

import 'ace-builds/src-noconflict/mode-python';
import 'ace-builds/src-noconflict/theme-monokai';

function CodeCell() {
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [image, setImage] = useState(null);
  const socket = useContext(KernelContext);

  useEffect(() => {
    if (socket) {
      socket.onmessage = (message) => {
        const data = JSON.parse(message.data);
        if (data.header.msg_type === 'execute_result' || data.header.msg_type === 'stream') {
          setOutput(prevOutput => prevOutput + '\n' + data.content.text);
        } else if (data.header.msg_type === 'display_data') {
          setImage(data.content.data['image/png']);
        }
      };
    }
  }, [socket]);

  const handleExecute = () => {
    if (socket && socket.readyState === 1) {
      const executeMessage = {
        header: {
          msg_id: `execute_${Date.now()}`,
          msg_type: 'execute_request',
          username: '',
          session: '',
        },
        parent_header: {},
        metadata: {},  
        content: {
          code,
          silent: false,
          store_history: true,
          user_expressions: {},
          allow_stdin: false,
        },
      };
      socket.send(JSON.stringify(executeMessage));
    } else {
      console.error('WebSocket is not open: readyState ', socket.readyState);
    }
  };

  return (
    <div>
      <AceEditor
        mode="python"
        theme="monokai"
        onChange={setCode}
        name="UNIQUE_ID_OF_DIV"
        editorProps={{ $blockScrolling: true }}
      />
      <button onClick={handleExecute} disabled={!socket}>
        Run
      </button>
      <pre>{output}</pre>
      {image && <img src={`data:image/png;base64,${image}`} alt="plot" />}
    </div>
  );
}

export default CodeCell;
